        neumorphism: "bg-background border-0 shadow-neu-inset focus:shadow-neu rounded-xl",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
);

export interface TextareaProps
  extends React.TextareaHTMLAttributes<HTMLTextAreaElement>,
    VariantProps<typeof textareaVariants> {}

const Textarea = React.forwardRef<HTMLTextAreaElement, TextareaProps>(
  ({ className, variant, ...props }, ref) => {
    return (
      <textarea
        className={cn(textareaVariants({ variant, className }))}
        ref={ref}
        {...props}
      />
    );
  }
);
Textarea.displayName = "Textarea";

export { Textarea, textareaVariants };

import * as React from "react";
import * as TogglePrimitive from "@radix-ui/react-toggle";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const toggleVariants = cva(
  "inline-flex items-center justify-center rounded-md text-sm font-medium ring-offset-background transition-colors hover:bg-muted hover:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 data-[state=on]:bg-accent data-[state=on]:text-accent-foreground",
  {
    variants: {
      variant: {
        default: "bg-transparent",
        outline: "border border-input bg-transparent hover:bg-accent hover:text-accent-foreground",
      },
      size: {
        default: "h-10 px-3",
        sm: "h-9 px-2.5",
        lg: "h-11 px-5",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

const Toggle = React.forwardRef<
  React.ElementRef<typeof TogglePrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof TogglePrimitive.Root> & VariantProps<typeof toggleVariants>
>(({ className, variant, size, ...props }, ref) => (
  <TogglePrimitive.Root ref={ref} className={cn(toggleVariants({ variant, size, className }))} {...props} />
));

Toggle.displayName = TogglePrimitive.Root.displayName;

export { Toggle, toggleVariants };

import { useState, useEffect } from "react";
import { DashboardLayout } from "@/components/DashboardLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";
import { Sparkles, TrendingUp, Users, Package, BarChart3, CheckCircle2, Clock } from "lucide-react";

interface Recommendation {
  id: string;
  type: string;
  title: string;
  description: string;
  priority: string;
  is_read: boolean;
  created_at: string;
  data: any;
}

const AIAssistant = () => {
  const { toast } = useToast();
  const [recommendations, setRecommendations] = useState<Recommendation[]>([]);
  const [loading, setLoading] = useState(true);
  const [generating, setGenerating] = useState(false);

  useEffect(() => {
    fetchRecommendations();
  }, []);

  const fetchRecommendations = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { data, error } = await supabase
        .from('ai_recommendations')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false })
        .limit(10);

      if (error) throw error;
      setRecommendations(data || []);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
      toast({
        title: "Erreur",
        description: "Impossible de charger les recommandations",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const generateRecommendations = async () => {
    setGenerating(true);
    try {
      const { data, error } = await supabase.functions.invoke('ai-recommendations');

      if (error) throw error;

      toast({
        title: "Recommandations générées",
        description: `${data.recommendations.length} nouvelles recommandations créées`,
      });

      fetchRecommendations();
    } catch (error: any) {
      console.error('Error generating recommendations:', error);
      toast({
        title: "Erreur",
        description: error.message || "Impossible de générer les recommandations",
        variant: "destructive",
      });
    } finally {
      setGenerating(false);
    }
  };

  const markAsRead = async (id: string) => {
    try {
      const { error } = await supabase
        .from('ai_recommendations')
        .update({ is_read: true })
        .eq('id', id);

      if (error) throw error;

      setRecommendations(prev =>
        prev.map(rec => rec.id === id ? { ...rec, is_read: true } : rec)
      );
    } catch (error) {
      console.error('Error marking as read:', error);
    }
  };

  const getTypeIcon = (type: string) => {
    const icons = {
      client_contact: Users,
      deal_priority: TrendingUp,
      product_suggestion: Package,
      analytics_insight: BarChart3
    };
    return icons[type as keyof typeof icons] || Sparkles;
  };

  const getPriorityColor = (priority: string) => {
    const colors = {
      urgent: "bg-destructive/10 text-destructive",
      high: "bg-warning/10 text-warning",
      medium: "bg-info/10 text-info",
      low: "bg-muted"
    };
    return colors[priority as keyof typeof colors] || "bg-muted";
  };

  const getTypeLabel = (type: string) => {
    const labels = {
      client_contact: "Contact Client",
      deal_priority: "Opportunité Prioritaire",
      product_suggestion: "Suggestion Produit",
      analytics_insight: "Insight Analytics"
    };
    return labels[type as keyof typeof labels] || type;
  };

  if (loading) {
    return (
      <DashboardLayout>
        <div className="flex items-center justify-center h-full">
          <div className="text-lg text-muted-foreground">Chargement...</div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout>
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-foreground flex items-center gap-3">
              <Sparkles className="h-8 w-8 text-primary" />
              Assistant IA
            </h1>
            <p className="text-muted-foreground mt-2">
              Recommandations intelligentes pour optimiser vos ventes
            </p>
          </div>
          <Button
            onClick={generateRecommendations}
            disabled={generating}
            className="gap-2"
          >
            <Sparkles className="h-4 w-4" />
            {generating ? "Génération en cours..." : "Générer Recommandations"}
          </Button>
        </div>

        {recommendations.length === 0 ? (
          <Card className="p-12 text-center">
            <Sparkles className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-lg font-semibold mb-2">Aucune recommandation</h3>
            <p className="text-muted-foreground mb-4">
              Générez des recommandations IA pour optimiser votre activité
            </p>
            <Button onClick={generateRecommendations} disabled={generating} className="gap-2">
              <Sparkles className="h-4 w-4" />
              Générer Recommandations
            </Button>
          </Card>
        ) : (
          <div className="space-y-4">
            {recommendations.map((rec) => {
              const Icon = getTypeIcon(rec.type);
              return (
                <Card
                  key={rec.id}
                  className={`p-6 transition-all hover:shadow-md ${
                    rec.is_read ? 'opacity-60' : ''
                  }`}
                >
                  <div className="flex items-start gap-4">
                    <div className="p-3 rounded-lg bg-primary/10">
                      <Icon className="h-6 w-6 text-primary" />
                    </div>

                    <div className="flex-1">
                      <div className="flex items-start justify-between mb-2">
                        <div>
                          <div className="flex items-center gap-2 mb-1">
                            <Badge variant="outline">{getTypeLabel(rec.type)}</Badge>
                            <Badge className={getPriorityColor(rec.priority)}>
                              {rec.priority.toUpperCase()}
                            </Badge>
                            {rec.is_read && (
                              <Badge variant="outline" className="gap-1">
                                <CheckCircle2 className="h-3 w-3" />
                                Lu
                              </Badge>
                            )}
                          </div>
                          <h3 className="text-lg font-semibold">{rec.title}</h3>
                        </div>
                        <div className="flex items-center gap-2 text-sm text-muted-foreground">
                          <Clock className="h-4 w-4" />
                          {new Date(rec.created_at).toLocaleDateString('fr-FR')}
                        </div>
                      </div>

                      <p className="text-muted-foreground mb-4">{rec.description}</p>

                      {!rec.is_read && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => markAsRead(rec.id)}
                          className="gap-2"
                        >
                          <CheckCircle2 className="h-4 w-4" />
                          Marquer comme lu
                        </Button>
                      )}
                    </div>
                  </div>
                </Card>
              );
            })}
          </div>
        )}
      </div>
    </DashboardLayout>
  );
};

export default AIAssistant;

import { useState, useEffect } from "react";
import { DashboardLayout } from "@/components/DashboardLayout";
import { Card } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { BarChart, Bar, LineChart, Line, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { TrendingUp, Users, DollarSign, Target, Download } from "lucide-react";
import { Button } from "@/components/ui/button";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";

const Analytics = () => {
  const { toast } = useToast();
  const [loading, setLoading] = useState(true);
  const [revenueData, setRevenueData] = useState<any[]>([]);
  const [topProducts, setTopProducts] = useState<any[]>([]);
  const [categoryData, setCategoryData] = useState<any[]>([]);
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalDeals: 0,
    totalCustomers: 0,
    conversionRate: 0,
  });

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      // Fetch sales data for revenue by month
      const { data: salesData, error: salesError } = await supabase
        .from("sales")
        .select("sale_date, total_price");

      if (salesError) throw salesError;

      // Group sales by month
      const monthlyRevenue = salesData?.reduce((acc: any, sale) => {
        const month = new Date(sale.sale_date).toLocaleDateString("fr-FR", {
          month: "short",
        });
        if (!acc[month]) {
          acc[month] = { month, revenue: 0, deals: 0 };
        }
        acc[month].revenue += Number(sale.total_price);
        acc[month].deals += 1;
        return acc;
      }, {});

      setRevenueData(Object.values(monthlyRevenue || {}));

      // Fetch top products
      const { data: productSales, error: productError } = await supabase
        .from("sales")
        .select(`
          product_id,
          total_price,
          quantity,
          products(name, category)
        `);

      if (productError) throw productError;

      // Group by product
      const productStats = productSales?.reduce((acc: any, sale) => {
        const productName = sale.products?.name || "Unknown";
        if (!acc[productName]) {
          acc[productName] = { name: productName, value: 0, quantity: 0 };
        }
        acc[productName].value += Number(sale.total_price);
        acc[productName].quantity += sale.quantity;
        return acc;
      }, {});

      const topProductsList = Object.values(productStats || {})
        .sort((a: any, b: any) => b.value - a.value)
        .slice(0, 5);

      setTopProducts(topProductsList);

      // Group by category
      const categoryStats = productSales?.reduce((acc: any, sale) => {
        const category = sale.products?.category || "Autre";
        if (!acc[category]) {
          acc[category] = { name: category, value: 0 };
        }
        acc[category].value += Number(sale.total_price);
        return acc;
      }, {});

      const categoryList = Object.values(categoryStats || {}).map((item: any, index) => ({
        ...item,
        color: ["#8B5CF6", "#F59E0B", "#3B82F6", "#10B981", "#22C55E"][index % 5],
      }));

      setCategoryData(categoryList);

      // Calculate overall stats
      const totalRevenue = salesData?.reduce((sum, sale) => sum + Number(sale.total_price), 0) || 0;

      const { data: dealsData } = await supabase
        .from("deals")
        .select("id, stage");

      const { data: customersData } = await supabase
        .from("customers")
        .select("id");

      const totalDeals = dealsData?.length || 0;
      const wonDeals = dealsData?.filter(d => d.stage === "gagne").length || 0;
      const conversionRate = totalDeals > 0 ? (wonDeals / totalDeals) * 100 : 0;

      setStats({
        totalRevenue,
        totalDeals,
        totalCustomers: customersData?.length || 0,
        conversionRate,
      });
    } catch (error) {
      console.error("Error fetching analytics:", error);
      toast({
        title: "Erreur",
        description: "Impossible de charger les analytics",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const exportToPDF = () => {
    toast({
      title: "Export en cours",
      description: "Fonctionnalité à venir",
    });
  };

  if (loading) {
    return (
      <DashboardLayout>
        <div className="flex items-center justify-center h-full">
          <div className="text-lg text-muted-foreground">Chargement...</div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout>
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-foreground">Analytics & Rapports</h1>
            <p className="text-muted-foreground mt-2">
              Visualisez vos performances et tendances
            </p>
          </div>
          <Button onClick={exportToPDF} variant="neumorphismPrimary" className="gap-2">
            <Download className="h-4 w-4" />
            Exporter PDF
          </Button>
        </div>

        {/* KPI Cards */}
        <div className="grid grid-cols-4 gap-4">
          <Card variant="neumorphism" className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Revenu Total</p>
                <p className="text-2xl font-bold text-foreground">
                  {stats.totalRevenue.toLocaleString("fr-FR")}€
                </p>
              </div>
              <DollarSign className="h-8 w-8 text-success" />
            </div>
          </Card>

          <Card variant="neumorphism" className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Opportunités</p>
                <p className="text-2xl font-bold text-foreground">{stats.totalDeals}</p>
              </div>
              <Target className="h-8 w-8 text-info" />
            </div>
          </Card>

          <Card variant="neumorphism" className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Clients</p>
                <p className="text-2xl font-bold text-foreground">{stats.totalCustomers}</p>
              </div>
              <Users className="h-8 w-8 text-primary" />
            </div>
          </Card>

          <Card variant="neumorphism" className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Taux de conversion</p>
                <p className="text-2xl font-bold text-foreground">
                  {stats.conversionRate.toFixed(1)}%
                </p>
              </div>
              <TrendingUp className="h-8 w-8 text-warning" />
            </div>
          </Card>
        </div>

        <Tabs defaultValue="revenue" className="w-full">
          <TabsList className="shadow-neu bg-background rounded-xl p-1">
            <TabsTrigger value="revenue">Revenus</TabsTrigger>
            <TabsTrigger value="pipeline">Pipeline</TabsTrigger>
            <TabsTrigger value="clients">Clients</TabsTrigger>
            <TabsTrigger value="conversion">Conversion</TabsTrigger>
          </TabsList>

          <TabsContent value="revenue">
            <Card variant="neumorphismFlat" className="p-6">
              <h3 className="text-lg font-semibold mb-4">Évolution du Revenu</h3>
              <ResponsiveContainer width="100%" height={400}>
                <LineChart data={revenueData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line type="monotone" dataKey="revenue" stroke="hsl(var(--primary))" strokeWidth={2} name="Revenu (€)" />
                  <Line type="monotone" dataKey="deals" stroke="hsl(var(--success))" strokeWidth={2} name="Deals" />
                </LineChart>
              </ResponsiveContainer>
            </Card>
          </TabsContent>

          <TabsContent value="pipeline">
            <Card variant="neumorphismFlat" className="p-6">
              <h3 className="text-lg font-semibold mb-4">Performance par Catégorie</h3>
              <ResponsiveContainer width="100%" height={400}>
                <PieChart>
                  <Pie
                    data={categoryData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, value }) => `${name}: ${value.toLocaleString("fr-FR")}€`}
                    outerRadius={120}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {categoryData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.color} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </Card>
          </TabsContent>

          <TabsContent value="clients">
            <Card variant="neumorphismFlat" className="p-6">
              <h3 className="text-lg font-semibold mb-4">Top 5 Produits les Plus Vendus</h3>
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={topProducts} layout="vertical">
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis type="number" />
                  <YAxis dataKey="name" type="category" width={150} />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="value" fill="hsl(var(--success))" name="Revenu (€)" />
                  <Bar dataKey="quantity" fill="hsl(var(--primary))" name="Quantité" />
                </BarChart>
              </ResponsiveContainer>
            </Card>
          </TabsContent>

          <TabsContent value="conversion">
            <Card variant="neumorphismFlat" className="p-6">
              <h3 className="text-lg font-semibold mb-4">Évolution du Revenu Mensuel</h3>
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={revenueData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="month" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="revenue" fill="hsl(var(--success))" name="Revenu (€)" />
                  <Bar dataKey="deals" fill="hsl(var(--primary))" name="Nombre de ventes" />
                </BarChart>
              </ResponsiveContainer>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </DashboardLayout>
  );
};

export default Analytics;

import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "@/integrations/supabase/client";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Cloud, Github } from "lucide-react";
import { Link } from "react-router-dom";
import { Separator } from "@/components/ui/separator";

const Auth = () => {
  const navigate = useNavigate();
  const { toast } = useToast();
  const [loading, setLoading] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fullName, setFullName] = useState("");

  useEffect(() => {
    // Check if user is already logged in
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session) {
        navigate("/");
      }
    });

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((event, session) => {
      if (session) {
        navigate("/");
      }
    });

    return () => subscription.unsubscribe();
  }, [navigate]);

  const handleSignUp = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const { error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          emailRedirectTo: `${window.location.origin}/`,
          data: {
            full_name: fullName,
          }
        }
      });

      if (error) throw error;

      toast({
        title: "Compte créé !",
        description: "Vous êtes maintenant connecté.",
      });
    } catch (error: any) {
      toast({
        title: "Erreur",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSignIn = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const { error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) throw error;

      toast({
        title: "Connexion réussie !",
        description: "Bienvenue sur Cloud Industrie.",
      });
    } catch (error: any) {
      toast({
        title: "Erreur de connexion",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignIn = async () => {
    setLoading(true);
    try {
      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'google',
        options: {
          redirectTo: `${window.location.origin}/`,
        }
      });
      if (error) throw error;
    } catch (error: any) {
      toast({
        title: "Erreur",
        description: error.message,
        variant: "destructive",
      });
      setLoading(false);
    }
  };

  const handleGitHubSignIn = async () => {
    setLoading(true);
    try {
      const { error } = await supabase.auth.signInWithOAuth({
        provider: 'github',
        options: {
          redirectTo: `${window.location.origin}/`,
        }
      });
      if (error) throw error;
    } catch (error: any) {
      toast({
        title: "Erreur",
        description: error.message,
        variant: "destructive",
      });
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary/5 via-background to-primary/10 flex items-center justify-center p-4">
      <div className="w-full max-w-md space-y-8">
        {/* Header */}
        <div className="text-center">
          <Link to="/" className="inline-flex items-center gap-2 mb-4">
            <Cloud className="h-8 w-8 text-primary" />
            <span className="text-2xl font-bold text-foreground">Cloud Industrie</span>
          </Link>
          <h1 className="text-3xl font-bold text-foreground">Bienvenue</h1>
          <p className="text-muted-foreground mt-2">
            Connectez-vous ou créez un compte
          </p>
        </div>

        {/* Auth Card */}
        <Card className="border-border/50">
          <CardHeader>
            <CardTitle>Authentification</CardTitle>
            <CardDescription>
              Accédez à votre espace Cloud Industrie
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Tabs defaultValue="signin" className="w-full">
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="signin">Connexion</TabsTrigger>
                <TabsTrigger value="signup">Inscription</TabsTrigger>
              </TabsList>

              <TabsContent value="signin">
                <form onSubmit={handleSignIn} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="signin-email">Email</Label>
                    <Input
                      id="signin-email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="votre@email.com"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="signin-password">Mot de passe</Label>
                    <Input
                      id="signin-password"
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      placeholder="••••••••"
                      required
                    />
                  </div>
                  <Button type="submit" className="w-full" disabled={loading}>
                    {loading ? "Connexion..." : "Se connecter"}
                  </Button>
                </form>
              </TabsContent>

              <TabsContent value="signup">
                <form onSubmit={handleSignUp} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="signup-name">Nom complet</Label>
                    <Input
                      id="signup-name"
                      type="text"
                      value={fullName}
                      onChange={(e) => setFullName(e.target.value)}
                      placeholder="Jean Dupont"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="signup-email">Email</Label>
                    <Input
                      id="signup-email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="votre@email.com"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="signup-password">Mot de passe</Label>
                    <Input
                      id="signup-password"
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      placeholder="••••••••"
                      required
                      minLength={6}
                    />
                  </div>
                  <Button type="submit" className="w-full" disabled={loading}>
                    {loading ? "Création..." : "Créer un compte"}
                  </Button>
                </form>
              </TabsContent>
            </Tabs>

            {/* OAuth Separator */}
            <div className="relative my-6">
              <div className="absolute inset-0 flex items-center">
                <Separator className="w-full" />
              </div>
              <div className="relative flex justify-center text-xs uppercase">
                <span className="bg-card px-2 text-muted-foreground">
                  Ou continuer avec
                </span>
              </div>
            </div>

            {/* OAuth Buttons */}
            <div className="grid grid-cols-2 gap-4">
              <Button
                variant="outline"
                onClick={handleGoogleSignIn}
                disabled={loading}
                className="w-full"
              >
                <svg className="mr-2 h-4 w-4" viewBox="0 0 24 24">
                  <path
                    d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
                    fill="#4285F4"
                  />
                  <path
                    d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
                    fill="#34A853"
                  />
                  <path
                    d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
                    fill="#FBBC05"
                  />
                  <path
                    d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
                    fill="#EA4335"
                  />
                </svg>
                Google
              </Button>
              <Button
                variant="outline"
                onClick={handleGitHubSignIn}
                disabled={loading}
                className="w-full"
              >
                <Github className="mr-2 h-4 w-4" />
                GitHub
              </Button>
            </div>
          </CardContent>
        </Card>

        <div className="text-center">
          <Button variant="link" asChild>
            <Link to="/">Retour à l'accueil</Link>
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Auth;

import { useState, useEffect } from "react";
import { useNavigate, useSearchParams, Link } from "react-router-dom";
import { motion } from "framer-motion";
import { supabase } from "@/integrations/supabase/client";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import {
    Cloud,
    Check,
    CreditCard,
    Shield,
    Lock,
    ArrowLeft,
    Sparkles,
    Zap,
    Building2,
    Crown,
    Loader2,
    Mail
} from "lucide-react";

// Plans data
const plansData = {
    free: {
        name: "Free",
        description: "Idéal pour découvrir la plateforme",
        monthlyPrice: 0,
        yearlyPrice: 0,
        icon: Sparkles,
        features: ["Chat IA limité (50 requêtes/mois)", "1 utilisateur", "Accès aux processus de base"],
    },
    pro: {
        name: "Pro",
        description: "Pour les équipes de production",
        monthlyPrice: 49,
        yearlyPrice: 39,
        icon: Zap,
        features: ["Chat IA illimité", "GPT-4 / Claude / Gemini", "5 utilisateurs inclus", "Support prioritaire"],
    },
    business: {
        name: "Business",
        description: "Solution multi-sites complète",
        monthlyPrice: 129,
        yearlyPrice: 99,
        icon: Building2,
        features: ["Tout le plan Pro", "Multi-usines illimitées", "Maintenance prédictive IA", "API illimitée"],
    },
    enterprise: {
        name: "Enterprise",
        description: "Solution sur-mesure",
        monthlyPrice: null,
        yearlyPrice: null,
        icon: Crown,
        features: ["Tout le plan Business", "IA personnalisée", "Support 24/7 dédié", "SLA garantie 99.9%"],
    },
};

type PlanId = keyof typeof plansData;

const Checkout = () => {
    const navigate = useNavigate();
    const [searchParams] = useSearchParams();
    const { toast } = useToast();

    const planId = (searchParams.get("plan") || "pro") as PlanId;
    const billingCycle = searchParams.get("billing") || "yearly";

    const [step, setStep] = useState<"auth" | "payment" | "success">("auth");
    const [loading, setLoading] = useState(false);
    const [user, setUser] = useState<any>(null);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [fullName, setFullName] = useState("");
    const [isLogin, setIsLogin] = useState(true);

    const plan = plansData[planId] || plansData.pro;
    const price = billingCycle === "yearly" ? plan.yearlyPrice : plan.monthlyPrice;
    const PlanIcon = plan.icon;

    useEffect(() => {
        supabase.auth.getSession().then(({ data: { session } }) => {
            if (session?.user) {
                setUser(session.user);
                setStep("payment");
            }
        });

        const { data: { subscription } } = supabase.auth.onAuthStateChange((event, session) => {
            if (session?.user) {
                setUser(session.user);
                setStep("payment");
            }
        });

        return () => subscription.unsubscribe();
    }, []);

    const handleGoogleSignIn = async () => {
        setLoading(true);
        try {
            const { error } = await supabase.auth.signInWithOAuth({
                provider: 'google',
                options: {
                    redirectTo: `${window.location.origin}/checkout?plan=${planId}&billing=${billingCycle}`,
                }
            });
            if (error) throw error;
        } catch (error: any) {
            toast({
                title: "Erreur",
                description: error.message,
                variant: "destructive",
            });
            setLoading(false);
        }
    };

    const handleEmailAuth = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);

        try {
            if (isLogin) {
                const { error } = await supabase.auth.signInWithPassword({
                    email,
                    password,
                });
                if (error) throw error;
            } else {
                const { error } = await supabase.auth.signUp({
                    email,
                    password,
                    options: {
                        emailRedirectTo: `${window.location.origin}/checkout?plan=${planId}&billing=${billingCycle}`,
                        data: { full_name: fullName },
                    }
                });
                if (error) throw error;
                toast({
                    title: "Compte créé !",
                    description: "Vous pouvez maintenant continuer.",
                });
            }
        } catch (error: any) {
            toast({
                title: "Erreur",
                description: error.message,
                variant: "destructive",
            });
        } finally {
            setLoading(false);
        }
    };

    const handlePayment = async () => {
        setLoading(true);

        // Simulate payment processing
        await new Promise(resolve => setTimeout(resolve, 2000));

        // In production, this would integrate with Stripe/WooCommerce
        toast({
            title: "Abonnement activé !",
            description: `Votre plan ${plan.name} est maintenant actif.`,
        });

        setStep("success");
        setLoading(false);
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-primary/5 via-background to-primary/10">
            {/* Header */}
            <header className="border-b border-border bg-background/80 backdrop-blur-lg">
                <div className="container mx-auto px-4 py-4">
                    <div className="flex items-center justify-between">
                        <Link to="/" className="flex items-center gap-2">
                            <Cloud className="h-6 w-6 text-primary" />
                            <span className="font-bold text-xl text-foreground">Cloud Industrie</span>
                        </Link>
                        <div className="flex items-center gap-2 text-sm text-muted-foreground">
                            <Lock className="h-4 w-4" />
                            <span>Paiement sécurisé</span>
                        </div>
                    </div>
                </div>
            </header>

            <main className="container mx-auto px-4 py-12">
                <Button variant="ghost" asChild className="mb-8">
                    <Link to="/pricing">
                        <ArrowLeft className="mr-2 h-4 w-4" />
                        Retour aux tarifs
                    </Link>
                </Button>

                <div className="grid lg:grid-cols-2 gap-12 max-w-5xl mx-auto">
                    {/* Left: Checkout Form */}
                    <div className="space-y-8">
                        {/* Progress Steps */}
                        <div className="flex items-center gap-4">
                            {[
                                { id: "auth", label: "Compte" },
                                { id: "payment", label: "Paiement" },
                                { id: "success", label: "Confirmation" },
                            ].map((s, i) => (
                                <div key={s.id} className="flex items-center">
                                    <div className={`
                    w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium
                    ${step === s.id || (step === "payment" && i === 0) || step === "success"
                                            ? "bg-primary text-primary-foreground"
                                            : "bg-muted text-muted-foreground"
                                        }
                  `}>
                                        {step === "success" || (step === "payment" && i === 0) ? (
                                            <Check className="h-4 w-4" />
                                        ) : (
                                            i + 1
                                        )}
                                    </div>
                                    <span className="ml-2 text-sm font-medium text-foreground hidden sm:block">
                                        {s.label}
                                    </span>
                                    {i < 2 && (
                                        <div className={`w-12 h-0.5 mx-3 ${(step === "payment" && i === 0) || step === "success"
                                                ? "bg-primary"
                                                : "bg-muted"
                                            }`} />
                                    )}
                                </div>
                            ))}
                        </div>

                        {/* Step: Auth */}
                        {step === "auth" && (
                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                            >
                                <Card>
                                    <CardHeader>
                                        <CardTitle>Créez votre compte</CardTitle>
                                        <CardDescription>
                                            Connectez-vous ou inscrivez-vous pour continuer
                                        </CardDescription>
                                    </CardHeader>
                                    <CardContent className="space-y-6">
                                        {/* Google Button */}
                                        <Button
                                            variant="outline"
                                            className="w-full h-12 text-base"
                                            onClick={handleGoogleSignIn}
                                            disabled={loading}
                                        >
                                            {loading ? (
                                                <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                                            ) : (
                                                <svg className="mr-3 h-5 w-5" viewBox="0 0 24 24">
                                                    <path
                                                        d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
                                                        fill="#4285F4"
                                                    />
                                                    <path
                                                        d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
                                                        fill="#34A853"
                                                    />
                                                    <path
                                                        d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
                                                        fill="#FBBC05"
                                                    />
                                                    <path
                                                        d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
                                                        fill="#EA4335"
                                                    />
                                                </svg>
                                            )}
                                            Continuer avec Google
                                        </Button>

                                        <div className="relative">
                                            <div className="absolute inset-0 flex items-center">
                                                <Separator />
                                            </div>
                                            <div className="relative flex justify-center text-xs uppercase">
                                                <span className="bg-card px-2 text-muted-foreground">
                                                    Ou avec votre email
                                                </span>
                                            </div>
                                        </div>

                                        {/* Email Form */}
                                        <form onSubmit={handleEmailAuth} className="space-y-4">
                                            {!isLogin && (
                                                <div className="space-y-2">
                                                    <Label htmlFor="fullName">Nom complet</Label>
                                                    <Input
                                                        id="fullName"
                                                        value={fullName}
                                                        onChange={(e) => setFullName(e.target.value)}
                                                        placeholder="Jean Dupont"
                                                        required={!isLogin}
                                                    />
                                                </div>
                                            )}
                                            <div className="space-y-2">
                                                <Label htmlFor="email">Email professionnel</Label>
                                                <Input
                                                    id="email"
                                                    type="email"
                                                    value={email}
                                                    onChange={(e) => setEmail(e.target.value)}
                                                    placeholder="vous@entreprise.com"
                                                    required
                                                />
                                            </div>
                                            <div className="space-y-2">
                                                <Label htmlFor="password">Mot de passe</Label>
                                                <Input
                                                    id="password"
                                                    type="password"
                                                    value={password}
                                                    onChange={(e) => setPassword(e.target.value)}
                                                    placeholder="••••••••"
                                                    required
                                                    minLength={6}
                                                />
                                            </div>
                                            <Button type="submit" className="w-full" disabled={loading}>
                                                {loading ? (
                                                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                                ) : (
                                                    <Mail className="mr-2 h-4 w-4" />
                                                )}
                                                {isLogin ? "Se connecter" : "Créer mon compte"}
                                            </Button>
                                        </form>

                                        <p className="text-center text-sm text-muted-foreground">
                                            {isLogin ? "Pas encore de compte ?" : "Déjà un compte ?"}{" "}
                                            <button
                                                type="button"
                                                onClick={() => setIsLogin(!isLogin)}
                                                className="text-primary hover:underline font-medium"
                                            >
                                                {isLogin ? "S'inscrire" : "Se connecter"}
                                            </button>
                                        </p>
                                    </CardContent>
                                </Card>
                            </motion.div>
                        )}

                        {/* Step: Payment */}
                        {step === "payment" && (
                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                            >
                                <Card>
                                    <CardHeader>
                                        <CardTitle className="flex items-center gap-2">
                                            <CreditCard className="h-5 w-5 text-primary" />
                                            Informations de paiement
                                        </CardTitle>
                                        <CardDescription>
                                            Connecté en tant que {user?.email}
                                        </CardDescription>
                                    </CardHeader>
                                    <CardContent className="space-y-6">
                                        <div className="space-y-4">
                                            <div className="space-y-2">
                                                <Label>Numéro de carte</Label>
                                                <Input placeholder="4242 4242 4242 4242" />
                                            </div>
                                            <div className="grid grid-cols-2 gap-4">
                                                <div className="space-y-2">
                                                    <Label>Date d'expiration</Label>
                                                    <Input placeholder="MM/AA" />
                                                </div>
                                                <div className="space-y-2">
                                                    <Label>CVC</Label>
                                                    <Input placeholder="123" />
                                                </div>
                                            </div>
                                            <div className="space-y-2">
                                                <Label>Nom sur la carte</Label>
                                                <Input placeholder="JEAN DUPONT" />
                                            </div>
                                        </div>

                                        <Separator />

                                        <div className="flex items-center gap-3 text-sm text-muted-foreground">
                                            <Shield className="h-5 w-5 text-success" />
                                            <span>Vos données sont chiffrées et sécurisées (SSL 256-bit)</span>
                                        </div>

                                        <Button
                                            className="w-full h-12 text-base"
                                            onClick={handlePayment}
                                            disabled={loading}
                                        >
                                            {loading ? (
                                                <Loader2 className="mr-2 h-5 w-5 animate-spin" />
                                            ) : (
                                                <Lock className="mr-2 h-5 w-5" />
                                            )}
                                            {price !== null
                                                ? `Payer ${price}€/${billingCycle === "yearly" ? "mois" : "mois"}`
                                                : "Confirmer l'abonnement"
                                            }
                                        </Button>
                                    </CardContent>
                                </Card>
                            </motion.div>
                        )}

                        {/* Step: Success */}
                        {step === "success" && (
                            <motion.div
                                initial={{ opacity: 0, scale: 0.95 }}
                                animate={{ opacity: 1, scale: 1 }}
                            >
                                <Card className="text-center">
                                    <CardContent className="pt-12 pb-8 space-y-6">
                                        <div className="w-20 h-20 bg-success/10 rounded-full flex items-center justify-center mx-auto">
                                            <Check className="h-10 w-10 text-success" />
                                        </div>
                                        <div>
                                            <h2 className="text-2xl font-bold text-foreground mb-2">
                                                Bienvenue dans Cloud Industrie !
                                            </h2>
                                            <p className="text-muted-foreground">
                                                Votre abonnement {plan.name} est maintenant actif.
                                            </p>
                                        </div>
                                        <Button size="lg" asChild>
                                            <Link to="/dashboard">
                                                Accéder au Dashboard
                                            </Link>
                                        </Button>
                                    </CardContent>
                                </Card>
                            </motion.div>
                        )}
                    </div>

                    {/* Right: Order Summary */}
                    <div className="lg:sticky lg:top-24 h-fit">
                        <Card>
                            <CardHeader>
                                <CardTitle>Récapitulatif</CardTitle>
                            </CardHeader>
                            <CardContent className="space-y-6">
                                {/* Plan Info */}
                                <div className="flex items-start gap-4">
                                    <div className="p-3 rounded-xl bg-primary/10">
                                        <PlanIcon className="h-6 w-6 text-primary" />
                                    </div>
                                    <div className="flex-1">
                                        <h3 className="font-semibold text-foreground">{plan.name}</h3>
                                        <p className="text-sm text-muted-foreground">{plan.description}</p>
                                        <Badge variant="secondary" className="mt-2">
                                            Facturation {billingCycle === "yearly" ? "annuelle" : "mensuelle"}
                                        </Badge>
                                    </div>
                                </div>

                                <Separator />

                                {/* Features */}
                                <div className="space-y-3">
                                    <p className="text-sm font-medium text-foreground">Inclus dans ce plan :</p>
                                    {plan.features.map((feature, i) => (
                                        <div key={i} className="flex items-center gap-2 text-sm text-muted-foreground">
                                            <Check className="h-4 w-4 text-success flex-shrink-0" />
                                            <span>{feature}</span>
                                        </div>
                                    ))}
                                </div>

                                <Separator />

                                {/* Pricing */}
                                <div className="space-y-3">
                                    {price !== null ? (
                                        <>
                                            <div className="flex justify-between text-sm">
                                                <span className="text-muted-foreground">Prix mensuel</span>
                                                <span className="text-foreground">{price}€</span>
                                            </div>
                                            {billingCycle === "yearly" && (
                                                <div className="flex justify-between text-sm">
                                                    <span className="text-muted-foreground">Réduction annuelle</span>
                                                    <span className="text-success">-20%</span>
                                                </div>
                                            )}
                                            <Separator />
                                            <div className="flex justify-between font-semibold text-lg">
                                                <span>Total</span>
                                                <span className="text-primary">
                                                    {billingCycle === "yearly"
                                                        ? `${price * 12}€/an`
                                                        : `${price}€/mois`
                                                    }
                                                </span>
                                            </div>
                                        </>
                                    ) : (
                                        <div className="text-center py-4">
                                            <p className="text-lg font-semibold text-foreground">Sur devis</p>
                                            <p className="text-sm text-muted-foreground">
                                                Notre équipe vous contactera
                                            </p>
                                        </div>
                                    )}
                                </div>

                                {/* Trust Badges */}
                                <div className="flex items-center justify-center gap-6 pt-4 border-t">
                                    <div className="flex items-center gap-1 text-xs text-muted-foreground">
                                        <Shield className="h-4 w-4" />
                                        <span>SSL Sécurisé</span>
                                    </div>
                                    <div className="flex items-center gap-1 text-xs text-muted-foreground">
                                        <Lock className="h-4 w-4" />
                                        <span>Paiement crypté</span>
                                    </div>
                                </div>
                            </CardContent>
                        </Card>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default Checkout;

